import { Component } from "react";
import React, { useState, useEffect } from "react";
import * as BooksAPI from "../BooksAPI";
import Book from "./Book"



function SearchBooks (props) {
    const { shelf, books, onChange } = props;
    const bookOnShelf = books.filter((book) => book.shelf === shelf.key);

    return (
        <div className="search-books">
        <div className="search-books-bar">
          <a to="/" className="close-search">Close</a>
          <div className="search-books-input-wrapper">
            <input type="text" placeholder="Search by title or author" onChange={(e) => this.props.updateQuery(e.target.value)} />
          </div>
        </div>
        <div className="search-books-results">
          <ol className="books-grid">
          {
            props.books ? (
            props.books.map(book => {
              return (
              <li key={book.id}>
                <Book
                id={book.id}
                shelf={book.shelf}
                addBook={() => props.addBook}
                title={book.title}
                authors={book.authors}
                url={book.imageLinks ? book.imageLinks.thumbnail : null}
                />
              </li>
              );
            })
            ) : (null)
          }
          </ol>
        </div>
        </div>
    );
  }


export default SearchBooks
