import "./App.css";
import { useState, useEffect } from "react";

import React from "react";
import * as BooksAPI from "./BooksAPI";
import Navbar from "./Components/Navbar";
import ShelfChange from "./Components/ShelfChange";
import BookShelf from "./Components/BookShelf";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [books, setBooks] = useState([]); // books

  // const BookShelf = (book, bookshelf) => {
  //   BooksAPI.update(book, bookshelf);
  //   if (bookshelf === "none") {
  //     // SetBooksFetch(BooksFetch.filter((book) => book.id !== book.id));
  //   } else {
  //     book.bookshelf = bookshelf;
  //     // SetBooksFetch(
  //     //   BooksFetch.filter((book) => book.id !== book.id).concat(book)
  //     // );
  //   }
  // };

  useEffect(() => {
    const FetchBooks = async () => {
      try {
        const response = await BooksAPI.getAll();
        setBooks(response);
      } catch (err) {
        console.log("Please Try Again");
      }
    };

    FetchBooks();
  }, []);

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a className="close-search">Close</a>

            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <Navbar />
          <div className="list-books-content">
            <div>
              <div className="open-search">
                <a to="/search">
                  <button>Add a book</button>
                </a>
              </div>
              <BookShelf books={books} shelf="currentlyReading" />
              <BookShelf books={books} shelf="wantToRead" />
              <BookShelf books={books} shelf="read" />
              {/* <ShelfChange /> */}
            </div>
          </div>
          ;
        </div>
      )}
    </div>
  );
}

export default App;
