import "./App.css";
import { useState, useEffect } from "react";

import React, { Component } from "react";
import Books from "./Components/Book";
import BooksList from "./Components/BooksList";
import Shelf from "./Components/BookShelf";
import ShelfChange from "./Components/ShelfChange";
import * as BooksAPI from "./BooksAPI";
import SearchBooks from "./Components/BooksSearch";
import BooksFetch from "./Components/BooksFetch";
import Navbar from "./Components/Navbar";
import BookShelfChange from "./Components/ShelfChange";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [BooksFetch, SetBooksFetch] = useState([]);

  useEffect(() => {
    const BooksFetch = async () => {
      const response = await BooksAPI.getAll();
      if (response.error) {
        console.log("Please Try Again");
      }
      BooksFetch(response);
      BooksFetch();
    };
  });

  const onChange = (book, bookshelf) => {
    BooksAPI.update(book, bookshelf);
    if (bookshelf === "none") {
      SetBooksFetch(BooksFetch.filter((book) => book.id !== book.id));
    } else {
      book.bookshelf = bookshelf;
      SetBooksFetch(
        BooksFetch.filter((book) => book.id !== book.id).concat(book)
      );
    }
  };

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a className="close-search">Close</a>

            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
        <div className="list-books">
          <Navbar />
          <div className="list-books-content">
            <div>
              <Bookshelf />
              <Shelfchange />
            </div>
          </div>
          ;
        </div>
      )}
    </div>
  );
}

export default App;
