import "./App.css";
import { useState, useEffect } from "react";
import React, { Component } from "react";
import Books from "./Components/Books";
import BooksList from "./Components/BooksList";
import Shelf from "./Components/Shelf";
import ShelfChange from "./Components/ShelfChange";
import * as BooksAPI from "./BooksAPI";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);
  const [books, setBooks] = useState([]);
  const [mapBooksID, setMapIDBooks] = useState(BooksList());

  useEffect(() => {
    BooksAPI.getAll().then((bookslist) => {
      setBooks(bookslist);
      map.BooksID(createBooksMap(bookslist));
    });
  }, []);

  useEffect(() => {
    const merge = searchBooks.map((Books) => {
      if (mapBooksID.has(book.id)) {
        return mapBooksID.get(book.id);
      } else {
        return Book;
      }
    });
    setMegeBooks(merge);
  }),
    [searchBooks];

  const onchange = (book, placeIt) => {
    const updateBooks = books.map((book) => {
      if (book.id === book.id) {
        book.shelf = placeIt;
        return book;
      }
      return book;
    });
    if (!mapBooksID.has(book.id)) {
      book.shelf = placeIT;
      updateBooks.push(book);
    }
    setBooks(updateBooks);
    BooksAPI.update(book, placeIt);
  };

  return (
    <div className="app">
      {showSearchPage ? (
        <div className="search-books">
          <div className="search-books-bar">
            <a
              className="close-search"
              onClick={() => setShowSearchpage(!showSearchPage)}
            >
              Close
            </a>
            <div className="search-books-input-wrapper">
              <input
                type="text"
                placeholder="Search by title, author, or ISBN"
              />
            </div>
          </div>
          <div className="search-books-results">
            <ol className="books-grid"></ol>
          </div>
        </div>
      ) : (
        <div>
          <bookslist />
          <shelfchange />
          <shelf />

          <div className="open-search">
            <a onClick={() => setShowSearchpage(!showSearchPage)}>Add a book</a>
          </div>
        </div>
      )}
    </div>
  );
}
